---------------------------------Kiểm tra tồn tại các bảng trong cơ sở dữ liệu QLHS-------------------------
select * from sys.tables whereexists (select * from sys.tables where name = 'HOCSINH') and exists (select * from sys.tables where name = 'QUATRINHHOC') and exists (select * from sys.tables where name = 'LOP') and exists (select * from sys.tables where name = 'KHOILOP') and exists (select * from sys.tables where name = 'NAMHOC') and exists (select * from sys.tables where name = 'HOCKY') and exists (select * from sys.tables where name = 'MONHOC') and exists (select * from sys.tables where name = 'LOAIDIEM') and exists (select * from sys.tables where name = 'CHUONGTRINHHOC') and exists (select * from sys.tables where name = 'BANGDIEM')and exists (select * from sys.tables where name = 'TONGKETMONHOC')and exists (select * from sys.tables where name = 'CT_TONGKETMONHOC')and exists (select * from sys.tables where name = 'TONGKETHOCKY')and exists (select * from sys.tables where name = 'BANGDIEM') and exists (select * from sys.tables where name = 'THAMSO')and exists (select * from sys.tables where name = 'GIOITINH')  and exists (select * from sys.tables where name = 'NGUOIDUNG') 